mapper.update:
update
    activity
set
    number = number - 1
where activity_id = #{activityId}
and start_time  <=  #{operateTime}
and end_time >= #{operateTime}
and number > 0

mapper.select:
select activity_id, name, number, start_time, end_time, create_time
from activity
where activity_id = #{activityId}
