mapper.update:
update
    product
set
    number = number - 1
where product_id = #{productId}
and start_time  <=  #{operateTime}
and end_time >= #{operateTime}
and number > 0

mapper.select:
select product_id, name, number, start_time, end_time, create_time
from product
where product_id = #{productId}
